mapper.select:
select c.challenge_id challengeId,c.title,c.mission,c.summary,c.manager_id managerId, u.nickname managerNickname,c.is_public isPublic,c.is_live isLive, c.category_id categoryId,c.max_user maxUser,c.now_user nowUser,c.start_date startDate, c.end_date endDate,c.hashtag,c.img_path imgPath from challenge c join user u on c.manager_id = u.user_id where c.is_public=1
and c.category_id=#{categoryId}
and c.is_live=#{isLive}
and c.hashtag like concat('%', #{hashtag}, '%')
order by c.start_date desc

mapper.update:
update challenge c set
c.title=#{title},c.mission=#{mission},c.summary=#{summary},c.is_public=#{isPublic},
c.is_live=#{isLive},c.category_id=#{categoryId},c.max_user=#{maxUser},c.now_user=#{nowUser},
c.start_date=#{startDate},c.end_date=#{endDate},c.hashtag=#{hashtag},c.img_path=#{imgPath}
where c.challenge_id=#{challengeId}

mapper.select:
select count(c.cmember_id)
from cmember c
where c.challenge_id=#{challengeId}

select c.cmember_id cmemberId,c.challenge_id challengeId,c.user_id userId,c.progress
from cmember c
where c.challenge_id=#{challengeId}
and c.user_id=#{userId}

mapper.update:
update cmember c set
c.progress=#{progress}
where c.challenge_id=#{challengeId}
and c.user_id=#{userId}

mapper.delete:
delete from cmember
where challenge_id=#{challengeId}
and user_id=#{userId}

mapper.select:
select count(c.challenge_id)
from challenge c
where c.category_id=#{categoryId}
