mapper.select:
select u.user_id userId,u.email,u.nickname,u.desc,u.profileimg_path profileimgPath,
		u.grade,u.point,u.regtime
from user u
where u.email=#{email}
and u.password=#{password}

select u.user_id,u.email,u.nickname,u.desc,u.profileimg_path,u.grade,u.point
from user u
where u.user_id=#{userId}

mapper.update:
update user u set

		password=#{password},

u.email=#{email},u.nickname=#{nickname},u.desc=#{desc},u.profileimg_path=#{profileimgPath}
where u.user_id=#{userId}

update notice n set
n.is_check=1
where n.notice_id=#{noticeId}
